mapper.update:
update cscenter
set writer = #{writer}, subject = #{subject}, content = #{content}, writedate = NOW()
where idx = #{idx}

mapper.select:
select idx, writer, subject, SUBSTR(content,1,10) as content, writedate from cscenter where 1=1
and idx = #{idx}
and writer = #{writer}
and writedate = #{writedate}
and idx = null and writer = '' and writedate = null
